import React, { useState, useEffect, useContext } from "react";
import { useParams, Link } from "react-router-dom";
import { IHomeworkIndexDTO } from "../../../domain/IHomeworkIndexDTO";
import { BaseCrudService } from "../../../base/BaseService";
import { IHomeworkDTO } from "../../../domain/IHomeworkDTO";
import { AppContext } from "../../../context/AppContext";

const HwTeacherIndex = () => {

    let { id } = useParams();
    const appContext = useContext(AppContext);
    const [items, setItems] = useState([] as IHomeworkDTO[]);
    const data = async () => await BaseCrudService
        .getEntities<IHomeworkDTO>('homeworks/teacherindexdto/' + id, appContext.jwt!)
        .then(data => setItems(data));

    useEffect(() => {
        data();
    });

    const deleteHw = async (id: string) => {
        await BaseCrudService.deleteEntity(id, 'homeworks/deletehomework');
        await data();
    }


    return (<div style={{paddingLeft: "100px", paddingRight: "100px"}}>
        <h1>Homeworks</h1>

        <p>
            <Link to={'/createhomework/' + id}>Create new homework</Link>
        </p>
        <table className="table">
            <thead>
                <tr>
                    <th>
                        Title
        </th>
                    <th>
                        Description
        </th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                {items.map(hw => (

                    <tr key={hw.id}>
                        <td>
                            {hw.title}
                        </td>
                        <td>
                            {hw.description}
                        </td>
                        <td>
                            <Link to={'/studenthwteacher/' + hw.id}>Responses</Link> |
                            <Link to={'/edithomework/' + hw.id}>Edit</Link>  
                            <button onClick={(e) => deleteHw(hw.id)}>Delete</button>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    </div>)
}

export default HwTeacherIndex;
