import React, { useState, useEffect, useContext } from "react";
import { useParams, Link } from "react-router-dom";
import { IHomeworkDTO } from "../../../domain/IHomeworkDTO";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { IStudentHomework } from "../../../domain/IStudentHomework";
import { AppContext } from "../../../context/AppContext";

const StudentHwTeacherIndex = () => {
    let { id } = useParams();
    const appContext = useContext(AppContext);

    const [items, setItems] = useState([] as IStudentHomework[]);
    const data = async () => await BaseCrudService
        .getEntities<IStudentHomework>('studenthomeworks/teacherindex/' + id, appContext.jwt!)
        .then(data => setItems(data));

    useEffect(() => {
        data();
    }, []);


    return (<>
        <h1>Student Homeworks</h1>


        <table className="table">
            <thead>
                <tr>
                    <th>
                        Student
            </th>
                    <th>
                        Grade
            </th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                {items.map(i => (
                    <tr key={i.id}>
                        <td>
                            {i.appUser?.firstName + " " + i.appUser?.lastName}
                        </td>
                        <th>
                            {i.grade}
                        </th>
                        <td>
                            <Link to={'/studenthwdetails/' + i.id}>Details</Link>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    </>)
}

export default StudentHwTeacherIndex;
