import React, { FormEvent, useState, useContext } from "react";
import { ILoginDTO } from "../../domain/ILoginDTO";
import { AccountApi } from "../../services/AccountApi";
import { AppContext } from "../../context/AppContext";

const Login = () => {
    const [props, setState] = useState({ email: '', password: '' } as ILoginDTO);
    const appContext = useContext(AppContext);

    const handleChange = (target: EventTarget & HTMLInputElement) => {
        setState({ ...props, [target.name]: target.value });
    }

    const handleSubmit = (e: FormEvent<HTMLFormElement>) => {
        let jwt: string | null = null;
        AccountApi.getJwt(props).then(data => jwt = data);
        console.log(jwt);
        if (jwt != null) {
            appContext.setJwt(jwt);
        }

        e.preventDefault();
    }

    return (
        <>
            <div className="row">
                <div className="col-md-6">
                    <h4>Use a local account to log in.</h4>

                    <form onSubmit={handleSubmit}>
                        <label htmlFor="email">Email</label>
                        <input className="form-control" type="email" id="email" onChange={(e) => handleChange(e.target)} />
                        <label htmlFor="password">Password</label>
                        <input
                            className="form-control"
                            type="password"
                            id="password"
                            onChange={(e) => handleChange(e.target)}
                        />
                        <button className="btn btn-primary" type="submit">Log in</button>
                    </form>
                </div>
            </div>
        </>
    );
}

export default Login;
