import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { AppContext } from "../../context/AppContext";



const Header = () => {
    const appContext = useContext(AppContext);

    function logOut(e: React.MouseEvent<HTMLAnchorElement, MouseEvent>) {
        appContext.setJwt('')
        appContext.setUserName('')
        appContext.setUserRole('')
        e.preventDefault();
    }

    function isLoggedIn(loggedIn: Boolean) {
        if (loggedIn) {
            return (
                <ul className="navbar-nav">
                    <li className="nav-item">
                        {appContext.userName}
                    </li>
                    <li className="nav-item">
                        <a onClick={(e) => logOut(e)}>Logout</a>
                    </li>
                </ul>
            );
        }

        return (
            <ul className="navbar-nav">
                <li className="nav-item">
                    <Link to="register">Register </Link>
                </li>
                <li className="nav-item">
                    <Link to="/login">Login </Link>
                </li>
            </ul>
        );
    }

    return (
        <>
            <header>
                <nav className="navbar navbar-expand-sm navbar-toggleable-sm navbar-light bg-white border-bottom box-shadow mb-3">
                    <div className="container">
                        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target=".navbar-collapse"
                            aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                            <span className="navbar-toggler-icon"></span>
                        </button>
                        <div className="navbar-collapse collapse d-sm-inline-flex flex-sm-row-reverse">
                            {isLoggedIn(appContext.jwt!.length > 0)}
                        </div>

                        <ul className="navbar-nav flex-grow-1">
                            <li className="nav-item">
                                <Link to="/">Home</Link>
                            </li>
                            <li className="nav-item">
                                <Link to="/dummies">Dummies </Link>
                            </li>
                        </ul>
                    </div>
                </nav>
            </header>
        </>
    );
}

export default Header;
