import React, { useState } from "react";
import { IDrinkCreate } from "../../../domain/IDrinkCreate";

interface IProps {
    drink: IDrinkCreate,
    handleChange: (target: EventTarget & HTMLInputElement) => void
}

const DrinkCreate = () => {
    const [props, setState] = useState({
        drink: {},
        handleChange: (target: EventTarget & HTMLInputElement) => {
            setState({ ...props, [target.name]: target.value });
        }
    } as IProps);
    
    return (
        <form>
            <div className="form-group">
                Name
                <input value={props.drink.name} onChange={(e) => props.handleChange(e.target)} />
            </div>
            <div className="form-group" >
                Price
                <input value={props.drink.price} onChange={(e) => props.handleChange(e.target)}/>
            </div>
            <div className="form-group">
                Image URL
                <input value={props.drink.imageURL} onChange={(e) => props.handleChange(e.target)}/>
            </div>
        </form>
    );
}

export default DrinkCreate;
