import React, { useState, useEffect, useContext } from "react";
import { IStudentSubject } from "../../../domain/IStudentSubjectDTO";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { useParams, Link } from "react-router-dom";
import Grade from "./grade";
import { AppContext } from "../../../context/AppContext";

const StudentSubjectIndex = () => {
    const [state, setItem] = useState([] as IStudentSubject[]);
    let { subjectid } = useParams();
    const appContext = useContext(AppContext);
    const data = async () => {
        await BaseCrudService
            .getEntities<IStudentSubject>('studentsubject/getstudentsubjects/' + subjectid, appContext.jwt!)
            .then(data => {
                setItem(data);
            });
    }

    useEffect(() => {
        data()
    }, [state.length]);

    async function processRequest(item: IStudentSubject, status: boolean) {
        item.accepted = status;
        await BaseCrudService.updateEntity<IStudentSubject>(item, 'studentsubject/edits');
        data();
    }

    function enrollStatus(item: IStudentSubject) {

        if (item.accepted != null || item.accepted != undefined) {
            return (<>{item.accepted ? 'Yes' : 'No'}</>);
        }

        return (<>
            <button onClick={() => processRequest(item, true)} >Accept</button>
            <button onClick={() => processRequest(item, false)}>Deny</button>
        </>);
    }

    function ifGrade(item: IStudentSubject) {
        if (item.accepted) {
            return <Grade item={item} />
        }

        return (<></>);
    }

    function averageGrade() {
        let sum = 0;
       let studentsubjects = state.filter(s => s.grade != null);
       studentsubjects.forEach(element => {
           sum += parseFloat(element.grade!)
       });

       return sum / studentsubjects.length;
    }

    return (<>
        <h1>Grade for</h1>

        <table className="table">
            <thead>
                <tr>
                    <th>
                        Student
        </th>
                    <th>
                        Subject
        </th>
                    <th>
                        Accepted
        </th>
                    <th>
                        Grade
        </th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                {state.map((ss, index) => (

                    <tr key={ss.id}>
                        <td>
                            {ss.appUser?.firstName + " " + ss.appUser?.lastName}
                        </td>
                        <td>
                            {ss.subject?.name}
                        </td>
                        <td>
                            {enrollStatus(ss)}
                        </td>
                        <td>
                            {ifGrade(ss)}
                        </td>
                    </tr>
                ))}
                <tr>
                    <td colSpan={3} style={{textAlign: "right"}}>
                        <b>Average: </b>
                    </td>
                    <td>
                        <b> {averageGrade()} out of 5</b>
                    </td>
                </tr>
                <tr>
                    <td colSpan={3} style={{textAlign: "right"}}>
                        <b>Not graded students count: </b>
                    </td>
                    <td>
                        <b> {state.filter(ss => ss.grade == null).length} ({state.filter(ss => ss.grade == null).length / state.length * 100}%)</b>
                    </td>
                </tr>
                <tr>
                    <td colSpan={3} style={{textAlign: "right"}}>
                        <b>Passed students: </b>
                    </td>
                    <td>
                        <b> {state.filter(ss => ss.grade != null && parseInt(ss.grade) > 0).length} ({state.filter(ss => ss.grade != null && parseInt(ss.grade) > 0).length / state.length * 100}%)</b>
                    </td>
                </tr>
                <tr>
                    <td colSpan={3} style={{textAlign: "right"}}>
                        <b>Failed students: </b>
                    </td>
                    <td>
                        <b>{state.filter(ss => ss.grade != null && parseInt(ss.grade) == 0).length} ({state.filter(ss => ss.grade != null && parseInt(ss.grade) == 0).length / state.length * 100}%)</b>
                    </td>
                </tr>
            </tbody>
        </table>
    </>);
}

export default StudentSubjectIndex;
