import React, { useState } from "react";
import Header from "./components/shared/Header";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Home from "./components/Home";
import { AppContextProvider, AppContextInitialState, IAppContext } from "./context/AppContext";
import Login from "./components/account/Login";
import Register from "./components/account/Register";
import SubjectCreate from "./components/views/subject/SubjectCreate";
import SubjectDetails from "./components/views/subject/SubjectDetails";
import SubjectIndex from "./components/views/subject/SubjectIndex";

const App = () => {
    const setJwt = (jwt: string) => {
        setAppState({...appState, jwt: jwt});
    }

    const setUserName = (userName: string) => {
        setAppState({...appState, userName: userName});
    }

    const setUserRole = (userRole: string) => {
        setAppState({...appState, userRole: userRole});
    }

    const initialAppState = {
        ...AppContextInitialState,
        setJwt,
        setUserName,
        setUserRole,
    } as IAppContext;
    const [appState, setAppState] = useState(initialAppState);

    return (
    <AppContextProvider value={appState}>
        <Router>
            <Header />
            <Switch>
                <Route exact path="/">
                    <Home />
                </Route>
                <Route path="/home">
                    <Home />
                </Route>
                <Route path="/subjects/create">
                    <SubjectCreate />
                </Route>
                <Route path="/subject/:id">
                    <SubjectDetails />
                </Route>
                <Route path="/subjects">
                    <SubjectIndex />
                </Route>
                <Route path="/login">
                    <Login />
                </Route>
                <Route path="/register">
                    <Register />
                </Route>
                <h1>404</h1>
            </Switch>
        </Router>
    </AppContextProvider>
    );
};

export default App;
