import React, { useState, useContext, useEffect } from "react";
import { AppContext } from "../../../context/AppContext";
import { BaseCrudService } from "../../../base/BaseCrudService";
import jwt_decode from "jwt-decode";
import { IStudentSubject } from "../../../domain/IStudentSubjectDTO";
import { Link } from "react-router-dom";

const SubjectIndexStudent = () => {

    const [subjects, setSubjects] = useState([] as IStudentSubject[]);
    const appContext = useContext(AppContext);
    const data = async () => await BaseCrudService
        .getEntities<IStudentSubject>('subjects/getstudentsubjects/' + jwt_decode<any>(appContext.jwt!)['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name'])
        .then(data => setSubjects(data));

    useEffect(() => {
        data();
    }, [subjects.length]);


    return (<div style={{paddingLeft: "100px", paddingRight: "100px"}}>
        <h1>My subjects</h1>

        <table className="table">
            <thead>
                <tr>
                    <th>
                        Name
                    </th>
                    <th>
                        Code
                    </th>
                    <th>
                        ECTS
                    </th>
                    <th>
                        Semester
                    </th>
                    <th>
                        Year
                    </th>
                    <th>
                        Grade
                    </th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                {subjects.map(s => (

                    <tr key={s.id}>
                        <td>
                            {s.subject!.name}
                        </td>
                        <td>
                            {s.subject!.code}
                        </td>
                        <td>
                            {s.subject!.ects}
                        </td>
                        <td>
                            {s.subject!.semester}
                        </td>
                        <td>
                            {s.subject!.year}
                        </td>
                        <td>
                            {s.grade}
                        </td>
                        <th>
                            <Link to={'/hwstudentindex/' + s.subject!.id} >Homeworks</Link>
                        </th>
                    </tr>
                ))}
            </tbody>
        </table>

    </div>);
}

export default SubjectIndexStudent;
