import React, { useState, useEffect, FormEvent, ChangeEvent } from "react";
import { IStudentSubject } from "../../../domain/IStudentSubjectDTO";
import { useParams } from "react-router-dom";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { ISubject } from "../../../domain/ISubject";


const Grade = () => {
    const [state, setState] = useState({} as IStudentSubject | null);
    let { id } = useParams();

    const data = async () => {
        await BaseCrudService
            .getEntity<IStudentSubject>(id, 'studentsubject/getstudentsubject')
            .then(data => setState(data));
    }


    useEffect(() => {
        data()
    }, [state?.id]);


  /*   const handleChange = (target: EventTarget & HTMLInputElement) => {
        setState({...state, [target.name]: target.value });
    }

    const handleSubmit = async () => {

        await BaseCrudService.updateEntity<IStudentSubject>(state.item, 'studentsubject/putstudentsubject')

    } */


    return (<>

        <h1>Grade for {state?.subject?.name}</h1>

        <table className="table">
            <thead>
                <tr>
                    <th>
                        Student
        </th>
                    <th>
                        Grade
        </th>
                    <th>New grade</th>
                </tr>
            </thead>
            <tbody>
                <tr >
                    <td>
                        {state?.appUser?.firstName + " " + state?.appUser?.lastName}
                    </td>
                    <td>
                        {state?.subject?.name}
                    </td>
                    {/* <td style={{ display: "inlineBlock", whiteSpace: "nowrap" }}>
                        <input value={state.grade} name="grade" onChange={(e) => handleChange(e.target)}  />
                        <button onClick={() => handleSubmit()} type="submit">Submit</button>
                    </td> */}
                </tr>
            </tbody>
        </table>

    </>);
}

export default Grade;
