import React, { useState, useContext, useEffect } from "react";
import { useParams } from "react-router-dom";
import { IStudentHomework } from "../../../domain/IStudentHomework";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { AppContext } from "../../../context/AppContext";
import jwt_decode from "jwt-decode";

const StudentHwStudentIndex = () => {
    let { id } = useParams();
    const appContext = useContext(AppContext);

    const [item, setItem] = useState({} as IStudentHomework);
    const data = async () => await BaseCrudService
        .getEntity<IStudentHomework>(id, 'studenthomeworks/GetStudentIndex/' + jwt_decode<any>(appContext.jwt!)['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name'])
        .then(data => setItem(data!));

    useEffect(() => {
        data();
    });

    useEffect(() => {
    }, [item.homework]);

    const handleChange = (target: EventTarget & HTMLTextAreaElement) => {
        setItem({ ...item, [target.name]: target.value });
    }

    const handleSubmit = async () => {
        /* await BaseCrudService
            .updateEntity<IHomeworkDTO>(item, 'homeworks/puthomework') */
        return alert('changes saved')
    }


    function answer() {
        if (item.studentAnswer != null) {
            return(<>
            <span>You have already submitted an answer: &nbsp;</span>
            <br/>
            <br/>
            <span>{item.studentAnswer}</span>
            </>);
        }

        return (<>
            <form onSubmit={() => handleSubmit()} >
                <div className="form-group">
                    <label>Student answer</label><br/>
                    <textarea value={item.studentAnswer} onChange={(e) => handleChange(e.target)} className="form-control" style={{width: "600px"}}></textarea>
                </div>
                <div className="form-group">
                    <button className="btn btn-primary" type="submit">Submit</button>
                </div>
            </form>
        </>);
    }

    return (<>
        <h1 style={{paddingLeft: "200px"}}>{item.homework?.title}</h1>

<div style={{paddingLeft: "200px"}}>
    <hr/>
    <dl className="row">
        <dt className="col-sm-2">
            Description
        </dt>
        <dd className="col-sm-10">
            {item.homework?.description}
        </dd>
        <dt className="col-sm-2">
            Grade
        </dt>
        <dd className="col-sm-10">
            {item.grade}
        </dd>
        <br/>
        <br/>
        {answer()}
    </dl>
</div>
<div style={{paddingLeft: "200px"}}>
    <a asp-controller="Homeworks" asp-action="StudentIndex" asp-route-id="@Model.Homework.SubjectId">Back to List</a>
</div>
    </>);
}

export default StudentHwStudentIndex;
