import React, { useState, useContext, useEffect } from "react";
import { AppContext } from "../../../context/AppContext";
import { BaseCrudService } from "../../../base/BaseCrudService";
import jwt_decode from "jwt-decode";
import { IStudentSubject } from "../../../domain/IStudentSubjectDTO";
import { Link } from "react-router-dom";

interface IState {
    subjects: IStudentSubject[];
    subjectsCopy: IStudentSubject[];
    year: number;
    semester: string;
}

const SubjectIndexStudent = () => {

    const [state, setState] = useState({subjects: [], subjectsCopy: [], year: 0, semester: ''} as IState);
    const appContext = useContext(AppContext);
    const data = async () => await BaseCrudService
        .getEntities<IStudentSubject>('subjects/getstudentsubjects/' + jwt_decode<any>(appContext.jwt!)['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name'], appContext.jwt!)
        .then(data => setState({...state, subjects: data, subjectsCopy: data}));

    useEffect(() => {
        data();
    }, []);

    function averageGrade() {
        let sum = 0;
       let studentsubjects =  state.subjectsCopy.filter(s => s.grade != null);
       studentsubjects.forEach(element => {
           sum += parseFloat(element.grade!)
       });

       return sum / studentsubjects.length;
    }

    function handleChange(e: EventTarget & HTMLSelectElement | EventTarget & HTMLInputElement) {
        setState({...state, [e.name]: e.value})
    }

    return (<div style={{ paddingLeft: "100px", paddingRight: "100px" }}>
        <h1>My subjects</h1>
        
        Choose semester <br/>
        <select name="semester" value={state.semester} onChange={(e) => handleChange(e.target)}>
            <option value="">Both</option>
            <option value="Spring">Spring</option>
            <option value="Autumn">Autumn</option>
        </select>
        <br/><br/>
        Choose year
        <input name="year" value={state.year} type="number" min={0} max={5000} onChange={(e) => handleChange(e.target)} />

        <table className="table">
            <thead>
                <tr>
                    <th>
                        Name
                    </th>
                    <th>
                        Code
                    </th>
                    <th>
                        ECTS
                    </th>
                    <th>
                        Semester
                    </th>
                    <th>
                        Year
                    </th>
                    <th>
                        Grade
                    </th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                {state.subjectsCopy.map(s => (

                    <tr key={s.id}>
                        <td>
                            {s.subject!.name}
                        </td>
                        <td>
                            {s.subject!.code}
                        </td>
                        <td>
                            {s.subject!.ects}
                        </td>
                        <td>
                            {s.subject!.semester}
                        </td>
                        <td>
                            {s.subject!.year}
                        </td>
                        <td>
                            {s.grade}
                        </td>
                        <th>
                            <Link to={'/hwstudentindex/' + s.subject!.id} >Homeworks</Link>
                        </th>
                    </tr>
                ))}
                <tr>
                    <td colSpan={5} style={{textAlign: "right"}}>
                        <b>Average grade: </b>
                    </td>
                    <td>
                        <b> {averageGrade()} out of 5</b>
                    </td>
                </tr>
            </tbody>
        </table>

    </div>);
}

export default SubjectIndexStudent;
