import React, { useState, useEffect } from "react";
import { IDrink } from "../../../domain/IDrink";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { Link } from "react-router-dom";

const DrinkIndex = () => {
    const [drinks, setDrinks] = useState([] as IDrink[]);

    const data = async () => await BaseCrudService
            .getEntities<IDrink>('drinks')
            .then(data => setDrinks(data));

    useEffect(() => {
        data();
    }, [drinks.length]);

    const deleteDrink = async (id: string) => {
        await BaseCrudService.deleteEntity(id, 'drinks');
        data();
    }

    return (
        <>
            <h1>Drinks</h1>
            <Link to="/drinks/create">Create new </Link>
            <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                <div>
                    {drinks.map(drink =>
                        <form key={drink.id}>
                            <div className="card">
                                <img className="card-img-top" src={drink.imageUrl} alt="" style={{ width: '17rem', height: '9rem' }} />
                                <div className="card-body">
                                    <h5 className="card-title">{drink.name}</h5>
                                    <p className="card-text"></p>
                                    <span style={{ display: 'none' }}></span>
                                    <input type="number" min="1" max="999" step="0" style={{ width: '60px' }} />
                                    <button className="btn btn-primary"></button>
                                    <Link to={'/drinks/' + drink.id}>Details</Link>
                                    <div onClick={(e) => deleteDrink(drink.id)}>Delete</div>
                                </div> 
                            </div>
                        </form>
                    )}
                </div>
            </div >

        </>
    );
}

export default DrinkIndex;
