import React, { useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";
import { IHomeworkDTO } from "../../../domain/IHomeworkDTO";
import { BaseCrudService } from "../../../base/BaseCrudService";

const EditHomework = () => {

    let { id } = useParams();
    const history = useHistory();
    const [item, setItem] = useState({} as IHomeworkDTO);

    const data = async () => await BaseCrudService
        .getEntity<IHomeworkDTO>(id, 'homeworks/gethomework')
        .then(data => setItem(data!));
    useEffect(() => {
        data()
    }, []);

    useEffect(() => {
        console.log(item)
    }, [item.description, item.title]);

    const handleChange = (target: EventTarget & HTMLInputElement) => {
        setItem({ ...item, [target.name]: target.value });
    }

    const handleSubmit = async () => {
        await BaseCrudService
            .updateEntity<IHomeworkDTO>(item, 'homeworks/puthomework')
        return alert('changes saved')
    }

    return (<>
        <h1>Edit</h1>

        <h4>Homework</h4>
        <hr />
        <div className="row">
            <div className="col-md-4">
                <div className="form-group">
                    <label className="control-label">Title</label>
                    <input value={item.title} name="title" className="form-control" onChange={(e) => handleChange(e.target)} />
                </div>
                <div className="form-group">
                    <label className="control-label">Description</label>
                    <input value={item.description} name="description" onChange={(e) => handleChange(e.target)} className="form-control" />
                </div>
                <div className="form-group">
                    <button onClick={() => handleSubmit()} type="submit">Submit</button>
                </div>
            </div>
        </div>

        <div>
            <a asp-action="Index">Back to List</a>
        </div>
    </>)
}

export default EditHomework;
