import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import { IHomeworkIndexDTO } from "../../../domain/IHomeworkIndexDTO";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { IHomeworkDTO } from "../../../domain/IHomeworkDTO";

const HwTeacherIndex = () => {

    let { id } = useParams();

    const [items, setItems] = useState([] as IHomeworkDTO[]);
    const data = async () => await BaseCrudService
        .getEntities<IHomeworkDTO>('homeworks/teacherindexdto/' + id)
        .then(data => setItems(data));

    useEffect(() => {
        data();
    }, [items.length]);

    const deleteHw = async (id: string) => {
        await BaseCrudService.deleteEntity(id, 'homeworks/deletehomework');
        await data();
    }


    return (<>
        <h1>Homeworks</h1>

        <p>
            <Link to={'/createhomework/' + id}>Create new homework</Link>
        </p>
        <table className="table">
            <thead>
                <tr>
                    <th>
                        Title
        </th>
                    <th>
                        Description
        </th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                {items.map(hw => (

                    <tr key={hw.id}>
                        <td>
                            {hw.title}
                        </td>
                        <td>
                            {hw.description}
                        </td>
                        <td>

                            <a asp-controller="StudentHomeworks" asp-action="TeacherIndex" asp-route-id="@item.Id">Responses</a> |
                            <a asp-action="Edit" asp-route-id="@item.Id">Edit</a> |
                            <a onClick={(e) => deleteHw(hw.id)}>Delete</a>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    </>)
}

export default HwTeacherIndex;
