import React, { useState, useEffect, FormEvent, ChangeEvent, useCallback, useContext } from "react";
import { IStudentSubject } from "../../../domain/IStudentSubjectDTO";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { AppContext } from "../../../context/AppContext";


interface IProps {
    item: IStudentSubject
}

const Grade = (stuff: IProps) => {
    const [state, setState] = useState(stuff.item as IStudentSubject);

    const appContext = useContext(AppContext);
    const handleChange = (target: EventTarget & HTMLInputElement) => {
        setState({ ...state, [target.name]: target.value });
    }

    const handleSubmit = async () => {
        await BaseCrudService.updateEntity<IStudentSubject>(state, 'studentsubject/edits', appContext.jwt!)
        return alert("changes saved");
    }

    useEffect(() => {
    }, [state.grade]);


    return (<>

            <input type="number" min="0" max="5" value={state.grade} name="grade" onChange={(e) => handleChange(e.target)} />
            <button onClick={() => handleSubmit()} type="submit">Submit</button>

    </>);
}

export default Grade;
