import React, { useState, useEffect, useContext } from "react";
import { IUser } from "../../../domain/IUser";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { AppContext } from "../../../context/AppContext";

const TeacherIndex = () => {
    const [teachers, setTeachers] = useState([] as IUser[]);
    const appContext = useContext(AppContext);
    const data = async () => await BaseCrudService
        .getEntities<IUser>('persons', appContext.jwt!)
        .then(data => setTeachers(data));

    useEffect(() => {
        data();
    }, [teachers.length]);

    return (
        <div style={{paddingLeft: "100px", paddingRight: "100px"}}>
            <h1>Teachers</h1>

            <table style={{paddingLeft: "100px"}} className="table">
                <thead>
                    <tr>
                        <th>
                            Email
        </th>
                        <th>
                            First name
        </th>
                        <th>
                            Last name
        </th>
                    </tr>
                </thead>
                <tbody>
                    {teachers.map(t => (

                        <tr key={t.id}>
                            <td>
                                {t.email}
                            </td>
                            <td>
                                {t.firstName}
                            </td>
                            <td>
                                {t.lastName}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default TeacherIndex;
