import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { IHomeworkIndexDTO } from "../../../domain/IHomeworkIndexDTO";
import { BaseCrudService } from "../../../base/BaseCrudService";

const HwTeacherIndex = () => {

    let { id } = useParams();

    const [items, setItems] = useState({} as IHomeworkIndexDTO);
    const data = async () => await BaseCrudService
        .getEntities<IHomeworkIndexDTO>('homeworks/teacherindexdto/' + id)
        .then(data => setItems({...items, ...data}));

    useEffect(() => {
        data();
    }, [items.homeworkDtos.length]);

    return (<>
        <h1>Homeworks for @Model.Subject.Name</h1>

        <p>
            <a asp-action="Create" asp-route-id="@Model.Subject.Id">Create new homework</a>
        </p>
        <table className="table">
            <thead>
                <tr>
                    <th>
                        Title
        </th>
                    <th>
                        Description
        </th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                ${}
                @foreach (var item in Model.Homeworks)
    {
                    <tr>
                        <td>
                            @Html.DisplayFor(modelItem => item.Title)
            </td>
                        <td>
                            @Html.DisplayFor(modelItem => item.Description)
            </td>
                        <td>

                            <a asp-controller="StudentHomeworks" asp-action="TeacherIndex" asp-route-id="@item.Id">Responses</a> |
                <a asp-action="Edit" asp-route-id="@item.Id">Edit</a> |
                <a asp-action="Delete" asp-route-id="@item.Id">Delete</a>
                        </td>
                    </tr>
                }
            </tbody>
        </table>
    </>)
}

export default HwTeacherIndex;
