import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { IHomeworkDTO } from "../../../domain/IHomeworkDTO";
import { BaseCrudService } from "../../../base/BaseCrudService";

const HomeworkStudentIndex = () => {
    let { id } = useParams();

    const [items, setItems] = useState([] as IHomeworkDTO[]);
    const data = async () => await BaseCrudService
        .getEntities<IHomeworkDTO>('homeworks/GetStudentIndex/' + id)
        .then(data => setItems(data));

    useEffect(() => {
        data();
    });

    return (
        <>
            <h1>Homeworks for @Model.Subject.Name</h1>

            <table className="table">
                <thead>
                    <tr>
                        <th>
                            Title
        </th>
                        <th>
                            Description
        </th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {items.map(hw => (
                        <tr key={hw.id}>
                            <td>
                                {hw.title}
                            </td>
                            <td>
                                {hw.description}
                            </td>
                            <td>
                                <a asp-controller="StudentHomeworks" asp-action="StudentIndex" asp-route-id="@item.Id">Details</a>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </>
    );
}

export default HomeworkStudentIndex;
