import React, { useState, useEffect } from "react";
import { IDrink } from "../../../domain/IDrink";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { Link } from "react-router-dom";

const DrinkIndex = () => {
    const [drinks, setDrinks] = useState([] as IDrink[]);

    useEffect(() => {
        async () => await BaseCrudService.getEntities<IDrink>('drinks').then(data => setDrinks(data))
    }, [drinks.length]);

    return (
        <>
            <h1>Drinks</h1>

            <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                <div>
                    {drinks.map(drink =>
                        <form >
                            <div className="card" style={{ width: '18rem', height: '18rem' }}>
                                <img className="card-img-top" src="${drink.imageUrl}" alt="Card image cap" style={{ width: '17rem', height: '9rem' }} />
                                <div className="card-body" style={{ width: '18rem', height: '9rem' }}>
                                    <h5 className="card-title"></h5>
                                    <p className="card-text"></p>
                                    <span style={{ display: 'none' }}></span>
                                    <input type="number" min="1" max="999" step="0" id="drinkAmount" name="drinkAmount" style={{ width: '60px' }} />
                                    <button className="btn btn-primary"></button>
                                    <Link to={'/drinks/' + drink.id}>Details</Link>
                                </div>
                            </div>
                        </form>
                    )}
                </div>
            </div >

        </>
    );
}

export default DrinkIndex;
