import React, { useState, useContext, useEffect } from "react";
import { useParams } from "react-router-dom";
import { IStudentHomework } from "../../../domain/IStudentHomework";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { AppContext } from "../../../context/AppContext";
import jwt_decode from "jwt-decode";

const StudentHwStudentIndex = () => {
    let { id } = useParams();
    const appContext = useContext(AppContext);

    const [item, setItem] = useState({} as IStudentHomework);
    const data = async () => await BaseCrudService
        .getEntity<IStudentHomework>(id, 'studenthomeworks/GetStudentIndex/' + jwt_decode<any>(appContext.jwt!)['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name'])
        .then(data => setItem(data!));

    useEffect(() => {
        data();
    }, []);

    function answer() {
        if (item.studentAnswer != null) {
            return(<>
            <span>You have already submitted an answer: &nbsp;</span>
            <br/>
            <br/>
            <span>{item.studentAnswer}</span>
            </>);
        }

        return (<>
            <form asp-action="Create">
                <div className="form-group">
                    <input type="hidden" asp-for="HomeworkId" value="@Model.HomeworkId"/>
                    <input type="hidden" asp-for="AppUserId" value="@Model.AppUserId"/>

                    <label asp-for="StudentAnswer"></label><br/>
                    <textarea className="form-control" style={{width: "600px"}} asp-for="StudentAnswer"></textarea>
                    <span asp-validation-for="StudentAnswer" className="text-danger"></span>
                </div>
                <div className="form-group">
                    <button className="btn btn-primary" type="submit">Submit</button>
                </div>
            </form>
        </>);
    }

    return (<>
        <h1>{item.homework?.title}</h1>

<div>
    <hr/>
    <dl className="row">
        <dt className="col-sm-2">
            Description
        </dt>
        <dd className="col-sm-10">
            {item.homework?.description}
        </dd>
        <dt className="col-sm-2">
            Grade
        </dt>
        <dd className="col-sm-10">
            {item.grade}
        </dd>
        <br/>
        <br/>
        {answer()}
    </dl>
</div>
<div>
    <a asp-controller="Homeworks" asp-action="StudentIndex" asp-route-id="@Model.Homework.SubjectId">Back to List</a>
</div>
    </>);
}

export default StudentHwStudentIndex;
