import React, { useState, FormEvent } from "react";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { useHistory } from "react-router-dom";
import { IDummy } from "../../../domain/IDummy";

const DummyCreate = () => {
    const history = useHistory();
    const [props, setState] = useState({id: '', dumbo: ''} as IDummy);
    
    const handleChange = (target: EventTarget & HTMLInputElement) => {
        setState({ ...props, [target.name]: target.value });
    }

    const handleSubmit = (e: FormEvent<HTMLFormElement>) => {
        BaseCrudService.createEntity(props, 'dummies');
        
        history.push('/dummies');
        e.preventDefault();
    }

    return (
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                dumbo
                <input value={props.dumbo} name="dumbo" onChange={(e) => handleChange(e.target)} />
            </div>
            <button type="submit">Submit</button>
        </form>
    );
}

export default DummyCreate;
