import React, { useState, useEffect, useContext } from "react";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { ISubject } from "../../../domain/ISubject";
import { AppContext } from "../../../context/AppContext";
import jwt_decode from "jwt-decode";

const SubjectIndex = () => {
    const [subjects, setSubjects] = useState([] as ISubject[]);
    const appContext = useContext(AppContext);
    const data = async () => await BaseCrudService
        .getEntities<ISubject>('subjects')
        .then(data => setSubjects(data));

    useEffect(() => {
        data();
    }, [subjects.length]);

    const deleteDrink = async (id: string) => {
        await BaseCrudService.deleteEntity(id, 'subjects');
        data();
    }

    function enrollOption(item: ISubject) {

        if (appContext.jwt == null) {
            return (<td></td>);
        }
        else {
            var subject = item.studentSubjects?.find(s => s.appUser.email == jwt_decode<any>(appContext.jwt!)['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name']) ?? null;
            if (jwt_decode<any>(appContext.jwt!)['http://schemas.microsoft.com/ws/2008/06/identity/claims/role'] == 'Teacher') {
            }
            else if (subject == null && appContext.jwt != null) {
                <td>
                    <a > Enroll </a>
                </td>
            }
            else if (subject!.accepted == null) {
                <td>Pending</td>
            }
            else if (subject!.accepted == true) {
                <td>Accepted</td>
            }
            else if (subject!.accepted == false) {
                <td>Denied</td>
            }
        }
    }

return (
        <>
        <h1>Subjects</h1>
        <table className="table">
            <thead>
                <tr>
                    <th>
                        Name
        </th>
                    <th>
                        Code
        </th>
                    <th>
                        ECTS
        </th>
                    <th>
                        Semester
        </th>
                    <th>
                        Year
        </th>
                    <th>
                        Description
        </th>
                    <th>
                        Students
        </th>
                </tr>
            </thead>
            <tbody>
                {subjects.map(subject => (

                    <tr>
                        <td>
                            {subject.name}
                        </td>
                        <td>
                            {subject.code}
                        </td>
                        <td>
                            {subject.ects}
                        </td>
                        <td>
                            {subject.semester}
                        </td>
                        <td>
                            {subject.year}
                        </td>
                        <td>
                            {subject.description}
                        </td>
                        <td>
                            {subject.studentSubjects.length}
                        </td>
                        {enrollOption(subject)}
                    </tr>
                    ))}

                </tbody>
            </table>

        </>
    );
}

export default SubjectIndex;
