import React, { useState } from "react";
import Header from "./components/shared/Header";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Home from "./components/Home";
import DrinkIndex from "./components/views/drink/DrinkIndex";
import DrinkDetails from "./components/views/drink/DrinkDetails";
import DrinkCreate from "./components/views/drink/DrinkCreate";
import { AppContextProvider, AppContextInitialState, IAppContext } from "./context/AppContext";
import Login from "./components/account/Login";

const App = () => {
    const setJwt = (jwt: string) => {
        setAppState({...appState, jwt: jwt});
    }

    const setUserName = (userName: string) => {
        setAppState({...appState, userName: userName});
    }

    const setUserRole = (userRole: string) => {
        setAppState({...appState, userRole: userRole});
    }

    const initialAppState = {...AppContextInitialState, setJwt, setUserName, setUserRole} as IAppContext;
    const [appState, setAppState] = useState(initialAppState);

    return (
    <AppContextProvider value={appState}>
        <Router>
            <Header />
            <Switch>
                <Route exact path="/">
                    <Home />
                </Route>
                <Route path="/home">
                    <Home />
                </Route>
                <Route path="/drinks/create">
                    <DrinkCreate />
                </Route>
                <Route path="/drinks/:id">
                    <DrinkDetails />
                </Route>
                <Route path="/drinks">
                    <DrinkIndex />
                </Route>
                <Route path="/login">
                    <Login />
                </Route>
                <h1>404</h1>
            </Switch>
        </Router>
    </AppContextProvider>
    );
};

export default App;
