import React, { useState, useEffect } from "react";
import { IStudentSubject } from "../../../domain/IStudentSubjectDTO";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { useParams } from "react-router-dom";

const StudentSubjectIndex = () => {
    const [state, setItem] = useState([] as IStudentSubject[]);
    let { subjectid } = useParams();
    const data = async () => {
        await BaseCrudService
            .getEntities<IStudentSubject>('studentsubject/getstudentsubjects/' + subjectid)
            .then(data => setItem(data));
    }

    let grades: any = [];

    useEffect(() => {
        data();
        grades.length = state.length;
        state.forEach((element, index) => {
            grades[index] = element.grade
        });
        console.log(grades)
    }, [state.length]);

    function processRequest(item: IStudentSubject, status: boolean) {
        item.accepted = status;
        const callChange = async () => await BaseCrudService.updateEntity<IStudentSubject>(item, 'studentsubject/putstudentsubject');
        callChange();
        data();
    }

    function enrollStatus(item: IStudentSubject) {

        if (item.accepted != null || item.accepted != undefined) {
            return (<>{item.accepted ? 'Yes' : 'No'}</>);
        }

        return (<>
            <button onClick={() => processRequest(item, true)} >Accept</button>
            <button onClick={() => processRequest(item, false)}>Deny</button>
        </>);
    }

    
    function updateGrade(item: IStudentSubject) {
        const callChange = async () => await BaseCrudService.updateEntity<IStudentSubject>(item, 'studentsubject/putstudentsubject');
        callChange();
        data();
    }

    function handleChange(target: EventTarget & HTMLInputElement, index: number) {
        grades[index] = target.value;
        console.log(grades);
    }
    
    function grade(index: number) {
        
        if (state[index].accepted == null || state[index].accepted == undefined) {
            return (<>None</>)
        }
        return (<>

            <div style={{display: "inlineBlock", whiteSpace: "nowrap"}}>
                <input value={grades[index]} onChange={(e) => handleChange(e.target, index)} min="0" max="5" className="form-control" name="grade" type="number" />
                <button onClick={() => updateGrade(state[index])}>Save</button>
            </div>
        </>
        );

    }

    return (<>
        <h1>Students list</h1>

        <table className="table">
            <thead>
                <tr>
                    <th>
                        Student
        </th>
                    <th>
                        Subject
        </th>
                    <th>
                        Accepted
        </th>
                    <th>
                        Grade
        </th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                {state.map((ss, index) => (

                    <tr key={ss.id}>
                        <td>
                            {ss.appUser?.firstName + " " + ss.appUser?.lastName}
                        </td>
                        <td>
                            {ss.subject?.name}
                        </td>
                        <td>
                            {enrollStatus(ss)}
                        </td>
                        <td style={{ display: "inlineBlock", whiteSpace: "nowrap" }}>
                            {grade(index)}
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    </>);
}

export default StudentSubjectIndex;
