import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { IStudentHomework } from "../../../domain/IStudentHomework";
import { BaseCrudService } from "../../../base/BaseCrudService";

const StudentHwDetails = () => {

    let { id } = useParams();
    const [item, setItem] = useState({} as IStudentHomework);

    const data = async () => await BaseCrudService
        .getEntity<IStudentHomework>(id, 'studenthomeworks/getstudenthomework')
        .then(data => setItem(data!));

    useEffect(() => {
        data()
    }, []);


    const handleChange = (target: EventTarget & HTMLInputElement) => {
        setItem({ ...item, [target.name]: target.value });
    }

    const handleSubmit = async () => {
        await BaseCrudService
            .updateEntity<IStudentHomework>(item, 'homeworks/puthomework')
        return alert('changes saved')
    }

    return (<>
        <div style={{padding: "50px"}}>
            <h4>StudentHomeworks</h4>
            <hr />
            <dl className="row">
                <dt className="col-sm-2">
                    Student
        </dt>
                <dd className="col-sm-10">
                    {item.appUser?.firstName + " " + item.appUser?.lastName}
        </dd>
                <dt className="col-sm-2">
                    Homework
        </dt>
                <dd className="col-sm-10">
                    {item.homework?.title}
        </dd>
                <dt className="col-sm-2">
                    Grade
        </dt>
                <dd className="col-sm-10">
                    {item.grade}
        </dd>
                <dt className="col-sm-2">
                    Student's answer
        </dt>
                <dd className="col-sm-10">
                    {item.studentAnswer}
        </dd>

                <form asp-action="Edit" asp-route-id="@Model.Id">
                    <input type="hidden" asp-for="@Model.Id" value="@Model.Id" />
                    <input type="hidden" asp-for="@Model.HomeworkId" value="@Model.HomeworkId" />
                    <input type="hidden" asp-for="@Model.AppUserId" value="@Model.AppUserId" />
                    <input type="hidden" asp-for="@Model.StudentAnswer" value="@Model.StudentAnswer" />
                    <div className="form-group">
                        <input min="0" max="5" className="form-control" asp-for="@Model.Grade" type="number" />
                        <span asp-validation-for="@Model.Grade" className="text-danger"></span>
                    </div>
                    <button type="submit">Save</button>
                </form>
            </dl>
        </div>
        <div>
            <a asp-action="TeacherIndex" asp-route-id="@Model.HomeworkId">Back to List</a>
        </div>
    </>);
}

export default StudentHwDetails;
