import React, { useState, useEffect, useContext } from "react";
import { IStudentSubject } from "../../../domain/IStudentSubjectDTO";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { AppContext } from "../../../context/AppContext";
import { ISubject } from "../../../domain/ISubject";
import jwt_decode from "jwt-decode";
import { IUser } from "../../../domain/IUser";


interface IProps {
    subjects: ISubject[];
}

const SubjectIndexTable = (stuff: IProps) => {
    const [state, setState] = useState(stuff.subjects as ISubject[]);

    const appContext = useContext(AppContext);

    const handleChange = (target: EventTarget & HTMLInputElement) => {
        setState({ ...state, [target.name]: target.value });
    }

    useEffect(() => {
        console.log(state)
    }, [state.length]);

    async function enroll(subject: ISubject, email: string) {
        var user: IUser = { email: email, id: 'x', firstName: '', lastName: '', code: '' };

        let dto: IStudentSubject = { subject: subject, appUser: user, accepted: null, id: '' };

        await BaseCrudService.createEntity<IStudentSubject>(dto, 'StudentSubject/PostStudentSubject');
        /* data(); */
    }

    function enrollOption(item: ISubject) {

        if (appContext.jwt == null) {
            return (<td></td>);
        }
        else {
            var subject = item.studentSubjects?.find(s => s != undefined && s!.appUser != undefined && s.appUser.email == jwt_decode<any>(appContext.jwt!)['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name']) ?? null;
            if (jwt_decode<any>(appContext.jwt!)['http://schemas.microsoft.com/ws/2008/06/identity/claims/role'] == 'Teacher') {
                return (<td></td>)
            }
            else if (subject == null && appContext.jwt != null) {
                return (<td>
                    <button onClick={() => enroll(item, jwt_decode<any>(appContext.jwt!)['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name'])} > Enroll </button>
                </td>);
            }
            else if (subject!.accepted == null) {
                return (<td>Pending</td>);
            }
            else if (subject!.accepted == true) {
                return (<td>Accepted</td>);
            }
            else if (subject!.accepted == false) {
                return (<td>Denied</td>);
            }
        }
        return (<td></td>);
    }
    return (<>

        {state.map(subject => (

            <tr key={subject.id}>
                <td>
                    {subject.name}
                </td>
                <td>
                    {subject.code}
                </td>
                <td>
                    {subject.ects}
                </td>
                <td>
                    {subject.semester}
                </td>
                <td>
                    {subject.year}
                </td>
                <td>
                    {subject.description}
                </td>
                <td>
                    {subject.studentSubjects?.filter(ss => ss.accepted).length}
                </td>
                {enrollOption(subject)}
            </tr>
        ))}

    </>);
}
export default SubjectIndexTable;
