import React, { useState } from "react";
import { IDrinkCreate } from "../../../domain/IDrinkCreate";


const DrinkCreate = () => {
    const [props, setState] = useState({name: '', price: '', imageUrl: ''} as IDrinkCreate);
    
    const handleChange = (target: EventTarget & HTMLInputElement) => {
        setState({ ...props, [target.name]: target.value });
        console.log(props)
    }

    return (
        <form>
            <div className="form-group">
                Name
                <input value={props.name} name="name" onChange={(e) => handleChange(e.target)} />
            </div>
            <div className="form-group" >
                Price
                <input value={props.price} name="price" onChange={(e) => handleChange(e.target)}/>
            </div>
            <div className="form-group">
                Image URL
                <input value={props.imageUrl} name="imageUrl" onChange={(e) => handleChange(e.target)}/>
            </div>
        </form>
    );
}

export default DrinkCreate;
