import React, { useState, useEffect, FormEvent, ChangeEvent } from "react";
import { IStudentSubject } from "../../../domain/IStudentSubjectDTO";
import { useParams } from "react-router-dom";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { ISubject } from "../../../domain/ISubject";

interface IGrade {
    random: ISubject[],
    item: IStudentSubject
}

const Grade = () => {
    const [state, setState] = useState({} as IGrade);
    let { id } = useParams();

    const data = async () => {
        await BaseCrudService
        .getEntities<ISubject>('subjects/getsubjects')
        .then(data => setState({...state, random: data}));

        await BaseCrudService
            .getEntity<IStudentSubject>(id, 'studentsubject/getstudentsubject')
            .then(data => setState({...state, item: data!}));
    }


    useEffect(() => {
        data()
    }, [state.random.length]);


    const handleChange = (target: EventTarget & HTMLInputElement) => {
        setState({...state, [target.name]: target.value });
    }

    const handleSubmit = async () => {

        await BaseCrudService.updateEntity<IStudentSubject>(state.item, 'studentsubject/putstudentsubject')

    }


    return (<>

        <h1>Grade for {state.item.subject?.name}</h1>

        <table className="table">
            <thead>
                <tr>
                    <th>
                        Student
        </th>
                    <th>
                        Grade
        </th>
                    <th>New grade</th>
                </tr>
            </thead>
            <tbody>
                <tr >
                    <td>
                        {state.item.appUser?.firstName + " " + state.item.appUser?.lastName}
                    </td>
                    <td>
                        {state.item.subject?.name}
                    </td>
                    <td style={{ display: "inlineBlock", whiteSpace: "nowrap" }}>
                        <input value={state.item.grade} name="grade" onChange={(e) => handleChange(e.target)}  />
                        <button onClick={() => handleSubmit()} type="submit">Submit</button>
                    </td>
                </tr>
            </tbody>
        </table>

    </>);
}

export default Grade;
