import React, { useState, useEffect } from "react";
import { IStudentSubject } from "../../../domain/IStudentSubjectDTO";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { useParams } from "react-router-dom";

const StudentSubjectIndex = () => {
    const [item, setItem] = useState({} as IStudentSubject);
    let { subjectid } = useParams();
    let studentSubjects: IStudentSubject[] = [];
    const data = async () => {
        await BaseCrudService
            .getEntities<IStudentSubject>('studentsubject/getstudentsubjects/' + subjectid)
            .then(data => studentSubjects = data);
    }

    useEffect(() => {
        data();
    }, [studentSubjects.length]);

    function processRequest(item: IStudentSubject, status: boolean) {
        item.accepted = status;
        const callChange = async () => await BaseCrudService.updateEntity<IStudentSubject>(item, 'studentsubject/putstudentsubject');
        callChange();
        data();
    }

    function enrollStatus(item: IStudentSubject) {

        if (item.accepted != null || item.accepted != undefined) {
            return (<>{item.accepted ? 'Yes' : 'No'}</>);
        }

        return (<>
            <button onClick={() => processRequest(item, true)} >Accept</button>
            <button onClick={() => processRequest(item, false)}>Deny</button>
        </>);
    }

    
    function updateGrade() {
        const callChange = async () => await BaseCrudService.updateEntity<IStudentSubject>(item, 'studentsubject/putstudentsubject');
        callChange();
        data();
    }

    function handleChange(target: EventTarget & HTMLInputElement) {
        setItem({ ...item, [target.name]: target.value });
    }
    
    function grade(item2: IStudentSubject) {
        setItem(item2);
        
        if (!item.accepted) {
            return (<>None</>)
        }

        return (<>

            <div style={{display: "inlineBlock", whiteSpace: "nowrap"}}>
                <input value={item.grade} onChange={(e) => handleChange(e.target)} min="0" max="5" className="form-control" name="grade" type="number" />
                <button   onClick={() => updateGrade()}>Save</button>
            </div>
        </>
        );

    }

    return (<>
        <h1>Students list</h1>

        <table className="table">
            <thead>
                <tr>
                    <th>
                        Student
        </th>
                    <th>
                        Subject
        </th>
                    <th>
                        Accepted
        </th>
                    <th>
                        Grade
        </th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                {studentSubjects.map(ss => (

                    <tr key={ss.id}>
                        <td>
                            {ss.appUser?.firstName + " " + ss.appUser?.lastName}
                        </td>
                        <td>
                            {ss.subject?.name}
                        </td>
                        <td>
                            {enrollStatus(ss)}
                        </td>
                        <td style={{ display: "inlineBlock", whiteSpace: "nowrap" }}>
                            {grade(ss)}
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    </>);
}

export default StudentSubjectIndex;
