import React, { useState, FormEvent } from "react";
import { IDrinkCreate } from "../../../domain/IDrinkCreate";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { useHistory } from "react-router-dom";

const DrinkCreate = () => {
    const history = useHistory();
    const [props, setState] = useState({name: '', price: '', imageUrl: ''} as IDrinkCreate);
    
    const handleChange = (target: EventTarget & HTMLInputElement) => {
        setState({ ...props, [target.name]: target.value });
    }

    const handleSubmit = (e: FormEvent<HTMLFormElement>) => {
        const create = async () => await BaseCrudService.createEntity(props, 'drinks');
        
        history.push('/drinks');
        e.preventDefault();
    }

    return (
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                Name
                <input value={props.name} name="name" onChange={(e) => handleChange(e.target)} />
            </div>
            <div className="form-group" >
                Price
                <input value={props.price} name="price" onChange={(e) => handleChange(e.target)}/>
            </div>
            <div className="form-group">
                Image URL
                <input value={props.imageUrl} name="imageUrl" onChange={(e) => handleChange(e.target)}/>
            </div>
            <button type="submit">Submit</button>
        </form>
    );
}

export default DrinkCreate;
