import React, { useState, useEffect } from "react";
import { IStudentSubject } from "../../../domain/IStudentSubjectDTO";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { useParams, Link } from "react-router-dom";
import Grade from "./grade";

const StudentSubjectIndex = () => {
    const [state, setItem] = useState([] as IStudentSubject[]);
    let { subjectid } = useParams();
    const data = async () => {
        await BaseCrudService
            .getEntities<IStudentSubject>('studentsubject/getstudentsubjects/' + subjectid)
            .then(data => {
                setItem(data);
            });
    }

    useEffect(() => {
        data()
    }, [state.length]);

    function processRequest(item: IStudentSubject, status: boolean) {
        item.accepted = status;
        const callChange = async () => await BaseCrudService.updateEntity<IStudentSubject>(item, 'studentsubject/edits');
        callChange();
        data();
    }

    function enrollStatus(item: IStudentSubject) {

        if (item.accepted != null || item.accepted != undefined) {
            return (<>{item.accepted ? 'Yes' : 'No'}</>);
        }

        return (<>
            <button onClick={() => processRequest(item, true)} >Accept</button>
            <button onClick={() => processRequest(item, false)}>Deny</button>
        </>);
    }
    
    function ifGrade(item: IStudentSubject) {
        if (item.accepted) {
            return <Grade item={item} />
        }

        return (<></>);
    }

    return (<>
        <h1>Grade for</h1>

        <table className="table">
            <thead>
                <tr>
                    <th>
                        Student
        </th>
                    <th>
                        Subject
        </th>
                    <th>
                        Accepted
        </th>
                    <th>
                        Grade
        </th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                {state.map((ss, index) => (

                    <tr key={ss.id}>
                        <td>
                            {ss.appUser?.firstName + " " + ss.appUser?.lastName}
                        </td>
                        <td>
                            {ss.subject?.name}
                        </td>
                        <td>
                            {enrollStatus(ss)}
                        </td>
                        <td>
                            {ifGrade(ss)}
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    </>);
}

export default StudentSubjectIndex;
