import React, { useEffect, useState, useContext } from "react";
import { IStudentHomework } from "../domain/IStudentHomework";
import { IStudentSubject } from "../domain/IStudentSubjectDTO";
import { AppContext } from "../context/AppContext";
import { BaseService } from "../base/BaseService";

interface IState {
    studentHomework: IStudentHomework[];
    studentSubjects: IStudentSubject[];
}

const Home = () => {
    const [teachers, setTeachers] = useState({studentHomework: [], studentSubjects: []} as IState);
    const appContext = useContext(AppContext);
    const data = async () => await BaseService
        .getEntities<IUser>('persons', appContext.jwt!)
        .then(data => setTeachers({...teachers, items: data, copyItems: data}))
        .then(BaseService.getEntities);

    useEffect(() => {
        data();
    }, []);

    useEffect(() => {
    }, [teachers.copyItems.length])


    function handleUpdate(target: EventTarget & HTMLInputElement) {
        setTeachers({...teachers, [target.name]: target.value})
    }

     async function handleSearch() {
        setTeachers({...teachers, copyItems: teachers.items.filter(s => (s.firstName + s.lastName + s.email).toLowerCase().includes(teachers.search.toLowerCase()))})
    }
    
    return (<h1 className="text-center">Welcome</h1>);
};

export default Home;
