import React, { useState, useEffect } from "react";
import { IUser } from "../../../domain/IUser";
import { BaseCrudService } from "../../../base/BaseCrudService";

const TeacherIndex = () => {
    const [teachers, setTeachers] = useState([] as IUser[]);
    const data = async () => await BaseCrudService
        .getEntities<IUser>('persons')
        .then(data => setTeachers(data));

    useEffect(() => {
        data();
    }, [teachers.length]);

    return (
        <>
            <h1>Teachers</h1>

            <table className="table" style={{paddingLeft: "100px"}}>
                <thead>
                    <tr>
                        <th>
                            Email
        </th>
                        <th>
                            First name
        </th>
                        <th>
                            Last name
        </th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {teachers.map(t => (

                        <tr key={t.id}>
                            <td>
                                {t.email}
                            </td>
                            <td>
                                {t.firstName}
                            </td>
                            <td>
                                {t.lastName}
                            </td>
                            <td>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </>
    );
}

export default TeacherIndex;
