import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { IHomeworkDTO } from "../../../domain/IHomeworkDTO";
import { BaseCrudService } from "../../../base/BaseCrudService";
import { IStudentHomework } from "../../../domain/IStudentHomework";

const StudentHwTeacherIndex = () => {
    let { id } = useParams();


    const [items, setItems] = useState([] as IStudentHomework[]);
    const data = async () => await BaseCrudService
        .getEntities<IStudentHomework>('homeworks/teacherindexdto/' + id)
        .then(data => setItems(data));

    useEffect(() => {
        data();
    });

    const deleteHw = async (id: string) => {
        await BaseCrudService.deleteEntity(id, 'homeworks/deletehomework');
        await data();
    }



    return (<>
        <h1>Student Homeworks</h1>


        <table className="table">
            <thead>
                <tr>
                    <th>
                        Student
            </th>
                    <th>
                        Grade
            </th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                {items.map(i => (
                    <tr key={i.id}>
                        <td>
                            {i.appUser?.firstName + " " + i.appUser?.lastName}
                        </td>
                        <th>
                            {i.grade}
                        </th>
                        <td>
                            <a asp-action="Details" asp-route-id="@item.Id">Details</a>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    </>)
}

export default StudentHwTeacherIndex;
